parameters.functionCode:
const prompt = $json.prompt || '';
const tone = $json.tone || 'professional';
const length = $json.length || 'medium';
const platform = $json.platform || 'facebook';
const hasOpenAI = !!$env.OPENAI_API_KEY && $env.OPENAI_API_KEY !== '';
return [{ json: { prompt, tone, length, platform, hasOpenAI } }];

