parameters.functionCode:
const item = items[0];
const data = item.json;
let contentJson = {};
try {
  const str = data.choices && data.choices[0] && data.choices[0].message && data.choices[0].message.content ? data.choices[0].message.content : '{}';
  contentJson = JSON.parse(str);
} catch (e) {
  contentJson = { error: 'Failed to parse OpenAI JSON content' };
}
return [{ json: {
  success: true,
  data: {
    content_text: contentJson.content_text || '',
    hashtags: Array.isArray(contentJson.hashtags) ? contentJson.hashtags.slice(0, 10) : [],
    suggested_images: Array.isArray(contentJson.suggested_images) ? contentJson.suggested_images : [],
    platform: $json.platform,
    tone: $json.tone,
    length: $json.length,
    generatedAt: new Date().toISOString()
  }
}}];

